use nom::IResult;
use nom::bytes::complete::{is_not, tag};
use nom::combinator::recognize;
use nom::multi::many0;
use nom::sequence::preceded;

pub fn parse_comment(input: &str) -> IResult<&'_ str, &'_ str> {
    recognize(preceded(tag("//"), many0(is_not("\r\n"))))(input)
}
